import {gql, useQuery} from '@apollo/client';

const getAuthorsQuery = gql`
  query GetAuthors{
    authors{
      id
      name
    }
  }
`;


function AddBook() {
  function displayAuthors() {
    const {loading,error,data} = useQuery(getAuthorsQuery);

    if(loading){
      return (
        <div>Loading Authors..</div>
      );
    }
    if(error){
      return (
        <div>Error..</div>
      );
    }

    return data.authors.map(author=>{
      <option key={author.id}>{author.name}</option>
    });
  }

  return (
    <form id="add-book">
      <div className="field">
        <label>Book name:</label>
        <input type="text"/>
      </div>

      <div className="field">
        <label>Genre:</label>
        <input type="text"/>
      </div>

      <div className="field">
        <label>Author</label>
        <select>
          {displayAuthors()}
        </select>
      </div>

      <button>+</button>
    </form>

  );
}

export default AddBook;
