import {gql} from '@apollo/client'

const getAuthorsQuery = gql`
  query GetAuthors{
    authors{
      id
      name
    }
  }
`;

const getBookListQuery = gql`
  query GetBooks{
    books{
      id
      name
    }
  }
`;

export {getAuthorsQuery,getBookListQuery};
