import {useQuery,gql} from '@apollo/client';

const getBookListQuery = gql`
    {
      books{
        name
        genre
        id
      }
    }
`
function BookList() {
  const {loading,error,data} = useQuery(getBookListQuery);
  return (
    <div>
      <ul id="book-list">
        {data.books.map(({name,genre,id})=> (
          <li>{name}</li>
        ))}
      </ul>
    </div>
  );
}

export default BookList;
