import {useQuery,gql} from '@apollo/client';

const getBookListQuery = gql`
    {
      books{
        name
        genre
        id
      }
    }
`
function BookList() {
  const {loading,error,data} = useQuery(getBookListQuery);
  return data.books.map(({name})=> (
          <li>{name}</li>
  ));
}

export default BookList;
