import {useQuery} from '@apollo/client';
import React,{useState} from 'react';
import {getAuthorsQuery} from '../queries/queries';

function DisplayAuthors() {
  const {loading,error,data} = useQuery(getAuthorsQuery);

  if(loading){
    return (
      <option disabled>Loading Authors..</option>
    );
  }
  if(error){
    return (
      <option disabled>Error..</option>
    );
  }

  return data.authors.map(author=>(
    <option key={author.id} value={author.id}>{author.name}</option>
  ));
}
function handleButtonClick(event,bookState) {
  event.preventDefault();
  console.log(bookState);
}
function AddBook() {
  const [bookState,setBookState] = useState({name: '',genre: '',authorId: ''});

  return (
    <form id="add-book" onSubmit={(e)=>{e.preventDefault();console.log(bookState);}}>
      <div className="field">
        <label>Book name:</label>
        <input type="text" onChange={(e)=>setBookState({name: e.target.value})}/>
      </div>

      <div className="field">
        <label>Genre:</label>
        <input type="text" onChange={(e)=>setBookState({genre: e.target.value})}/>
      </div>

      <div className="field">
        <label>Author</label>
        <select onChange={(e)=>setBookState({authorId: e.target.value})}>
          <option disabled>Select Author</option>
          {DisplayAuthors()}
        </select>
      </div>

      <button>+</button>
    </form>

  );
}

export default AddBook;
